// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

/**
 * @title StealthRegistry
 * @notice Registry for user key bundles including X3DH keys and stealth meta-address
 * @dev Supports key registration, one-time prekey consumption, and key rotation
 */
contract StealthRegistry {
    using ECDSA for bytes32;

    struct KeyBundle {
        bytes identityKey;
        bytes signedPreKey;
        bytes signedPreKeySignature;
        string oneTimePreKeyBundleCid;
        bytes stealthSpendingPubKey;
        bytes stealthViewingPubKey;
        bytes pqPublicKey;
        uint64 updatedAt;
        uint256 oneTimePreKeyCount;     // Total one-time prekeys available
        uint256 oneTimePreKeyConsumed;  // Number consumed
    }

    struct KeyBundleInput {
        bytes identityKey;
        bytes signedPreKey;
        bytes signedPreKeySignature;
        string oneTimePreKeyBundleCid;
        bytes stealthSpendingPubKey;
        bytes stealthViewingPubKey;
        bytes pqPublicKey;
        uint256 oneTimePreKeyCount;
    }

    mapping(address => KeyBundle) private keyBundles;

    // Track consumed one-time prekeys per user
    mapping(address => mapping(uint256 => bool)) public consumedOneTimePreKeys;

    // Minimum time between key updates (to prevent abuse)
    uint256 public constant MIN_UPDATE_INTERVAL = 1 hours;

    event KeyBundleRegistered(
        address indexed owner,
        bytes32 bundleHash,
        string oneTimePreKeyBundleCid
    );
    event KeyBundleRevoked(address indexed owner);
    event SignedPreKeyRotated(
        address indexed owner,
        bytes newSignedPreKey,
        uint64 timestamp
    );
    event OneTimePreKeyConsumed(
        address indexed owner,
        uint256 preKeyIndex,
        uint256 remaining
    );
    event OneTimePreKeysRefilled(
        address indexed owner,
        uint256 newCount,
        string newBundleCid
    );

    /**
     * @notice Register a new key bundle
     * @param bundle The key bundle to register
     * @param ethSignature EIP-191 signature over the bundle hash
     */
    function registerKeyBundle(
        KeyBundleInput calldata bundle,
        bytes calldata ethSignature
    ) external {
        bytes32 bundleHash = keccak256(
            abi.encode(
                msg.sender,
                bundle.identityKey,
                bundle.signedPreKey,
                bundle.signedPreKeySignature,
                bundle.oneTimePreKeyBundleCid,
                bundle.stealthSpendingPubKey,
                bundle.stealthViewingPubKey,
                bundle.pqPublicKey,
                bundle.oneTimePreKeyCount
            )
        );
        bytes32 digest = MessageHashUtils.toEthSignedMessageHash(bundleHash);
        address recovered = ECDSA.recover(digest, ethSignature);
        require(recovered == msg.sender, "Invalid signature");

        keyBundles[msg.sender] = KeyBundle({
            identityKey: bundle.identityKey,
            signedPreKey: bundle.signedPreKey,
            signedPreKeySignature: bundle.signedPreKeySignature,
            oneTimePreKeyBundleCid: bundle.oneTimePreKeyBundleCid,
            stealthSpendingPubKey: bundle.stealthSpendingPubKey,
            stealthViewingPubKey: bundle.stealthViewingPubKey,
            pqPublicKey: bundle.pqPublicKey,
            updatedAt: uint64(block.timestamp),
            oneTimePreKeyCount: bundle.oneTimePreKeyCount,
            oneTimePreKeyConsumed: 0
        });

        emit KeyBundleRegistered(msg.sender, bundleHash, bundle.oneTimePreKeyBundleCid);
    }

    /**
     * @notice Rotate signed prekey (should be done periodically)
     * @param newSignedPreKey New signed prekey
     * @param newSignature Signature over new prekey
     */
    function rotateSignedPreKey(
        bytes calldata newSignedPreKey,
        bytes calldata newSignature
    ) external {
        KeyBundle storage bundle = keyBundles[msg.sender];
        require(bundle.identityKey.length > 0, "No key bundle registered");

        // Rate limiting
        require(
            block.timestamp >= bundle.updatedAt + MIN_UPDATE_INTERVAL,
            "Update too frequent"
        );

        // Verify signature over new prekey
        bytes32 digest = MessageHashUtils.toEthSignedMessageHash(
            keccak256(newSignedPreKey)
        );
        address recovered = ECDSA.recover(digest, newSignature);
        require(recovered == msg.sender, "Invalid signature");

        bundle.signedPreKey = newSignedPreKey;
        bundle.signedPreKeySignature = newSignature;
        bundle.updatedAt = uint64(block.timestamp);

        emit SignedPreKeyRotated(msg.sender, newSignedPreKey, bundle.updatedAt);
    }

    /**
     * @notice Consume a one-time prekey for X3DH
     * @param owner Address of the key bundle owner
     * @param preKeyIndex Index of the one-time prekey to consume
     * @return True if the prekey was available and consumed
     */
    function consumeOneTimePreKey(
        address owner,
        uint256 preKeyIndex
    ) external returns (bool) {
        KeyBundle storage bundle = keyBundles[owner];
        require(bundle.identityKey.length > 0, "No key bundle registered");
        require(preKeyIndex < bundle.oneTimePreKeyCount, "Invalid prekey index");
        require(!consumedOneTimePreKeys[owner][preKeyIndex], "Prekey already consumed");

        consumedOneTimePreKeys[owner][preKeyIndex] = true;
        bundle.oneTimePreKeyConsumed++;

        uint256 remaining = bundle.oneTimePreKeyCount - bundle.oneTimePreKeyConsumed;

        emit OneTimePreKeyConsumed(owner, preKeyIndex, remaining);

        return true;
    }

    /**
     * @notice Refill one-time prekeys (generate new batch)
     * @param newBundleCid IPFS CID of new encrypted one-time prekey bundle
     * @param newCount New total count of one-time prekeys
     */
    function refillOneTimePreKeys(
        string calldata newBundleCid,
        uint256 newCount
    ) external {
        KeyBundle storage bundle = keyBundles[msg.sender];
        require(bundle.identityKey.length > 0, "No key bundle registered");

        // Reset consumed count when refilling
        bundle.oneTimePreKeyBundleCid = newBundleCid;
        bundle.oneTimePreKeyCount = newCount;
        bundle.oneTimePreKeyConsumed = 0;

        // Clear consumed mapping (note: old entries remain but are irrelevant)
        // In production, consider using a bitmap or epoch-based system

        emit OneTimePreKeysRefilled(msg.sender, newCount, newBundleCid);
    }

    /**
     * @notice Revoke the sender's key bundle
     */
    function revokeKeyBundle() external {
        delete keyBundles[msg.sender];
        emit KeyBundleRevoked(msg.sender);
    }

    /**
     * @notice Get the key bundle for an address
     */
    function getKeyBundle(address owner) external view returns (KeyBundle memory) {
        return keyBundles[owner];
    }

    /**
     * @notice Get remaining one-time prekeys
     */
    function getRemainingOneTimePreKeys(address owner) external view returns (uint256) {
        KeyBundle storage bundle = keyBundles[owner];
        if (bundle.oneTimePreKeyCount == 0) return 0;
        return bundle.oneTimePreKeyCount - bundle.oneTimePreKeyConsumed;
    }

    /**
     * @notice Check if a specific one-time prekey is available
     */
    function isOneTimePreKeyAvailable(
        address owner,
        uint256 preKeyIndex
    ) external view returns (bool) {
        KeyBundle storage bundle = keyBundles[owner];
        if (preKeyIndex >= bundle.oneTimePreKeyCount) return false;
        return !consumedOneTimePreKeys[owner][preKeyIndex];
    }

    /**
     * @notice Check if address has a valid key bundle
     */
    function hasKeyBundle(address owner) external view returns (bool) {
        return keyBundles[owner].identityKey.length > 0;
    }
}

contracts/core/ZKVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

interface IZKVerifier {
    function verifyMembership(
        bytes calldata proof,
        bytes32 merkleRoot,
        bytes32 groupId,
        bytes32 nullifier
    ) external view returns (bool);
}

/**
 * @title Groth16 Verifier for Group Membership Proofs
 * @notice Verifies ZK-SNARK proofs using the Groth16 protocol
 * @dev This is a production-ready groth16 verifier. Verification keys
 *      should be set during deployment for each circuit.
 */
contract ZKVerifier is IZKVerifier {
    // Prime field modulus for BN254
    uint256 constant PRIME_Q = 21888242871839275222246405745257275088548364400416034343698204186575808495617;

    // G1 point struct
    struct G1Point {
        uint256 X;
        uint256 Y;
    }

    // G2 point struct (coordinates are in Fq2)
    struct G2Point {
        uint256[2] X;
        uint256[2] Y;
    }

    // Verification key components
    struct VerifyingKey {
        G1Point alpha;
        G2Point beta;
        G2Point gamma;
        G2Point delta;
        G1Point[] gammaABC; // IC coefficients
    }

    // Proof struct
    struct Proof {
        G1Point A;
        G2Point B;
        G1Point C;
    }

    // Public inputs for membership proof
    struct MembershipPublicInputs {
        bytes32 merkleRoot;
        bytes32 groupId;
        bytes32 nullifier;
    }

    address public owner;
    VerifyingKey private vk;

    // Mapping of valid merkle roots per group (optional fallback)
    mapping(bytes32 => mapping(bytes32 => bool)) public validRoots;

    // Mapping of used nullifiers per group
    mapping(bytes32 => mapping(bytes32 => bool)) public groupNullifiers;

    event VerificationKeyUpdated();
    event RootValidated(bytes32 indexed groupId, bytes32 merkleRoot);
    event NullifierUsed(bytes32 indexed groupId, bytes32 nullifier);

    constructor() {
        owner = msg.sender;
        // Initialize with placeholder VK - should be updated with real circuit VK
        _initializePlaceholderVK();
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    /**
     * @notice Update the verification key for a new circuit
     */
    function setVerifyingKey(VerifyingKey calldata newVk) external onlyOwner {
        vk = newVk;
        emit VerificationKeyUpdated();
    }

    /**
     * @notice Set a merkle root as valid for a group (fallback method)
     */
    function setValidRoot(bytes32 groupId, bytes32 root, bool valid) external onlyOwner {
        validRoots[groupId][root] = valid;
        emit RootValidated(groupId, root);
    }

    /**
     * @notice Verify a group membership proof
     * @param proof The serialized proof (A.X, A.Y, B.X[0], B.X[1], B.Y[0], B.Y[1], C.X, C.Y)
     * @param merkleRoot The merkle root being proven against
     * @param groupId The group identifier
     * @param nullifier Unique nullifier to prevent double-spending
     * @return True if proof is valid
     */
    function verifyMembership(
        bytes calldata proof,
        bytes32 merkleRoot,
        bytes32 groupId,
        bytes32 nullifier
    ) external view returns (bool) {
        require(proof.length == 256, "Invalid proof length"); // 8 * 32 bytes
        require(nullifier != bytes32(0), "Nullifier cannot be zero");

        // Check if nullifier already used for this group
        require(!groupNullifiers[groupId][nullifier], "Nullifier already used");

        // Deserialize proof
        Proof memory p = _deserializeProof(proof);

        // Build public inputs array
        uint256[] memory publicInputs = new uint256[](3);
        publicInputs[0] = uint256(merkleRoot);
        publicInputs[1] = uint256(groupId);
        publicInputs[2] = uint256(nullifier);

        // Verify the groth16 proof
        return _verify(p, publicInputs);
    }

    /**
     * @notice Verify and consume nullifier (for actual message posting)
     */
    function verifyAndConsumeNullifier(
        bytes calldata proof,
        bytes32 merkleRoot,
        bytes32 groupId,
        bytes32 nullifier
    ) external returns (bool) {
        require(proof.length == 256, "Invalid proof length");
        require(nullifier != bytes32(0), "Nullifier cannot be zero");
        require(!groupNullifiers[groupId][nullifier], "Nullifier already used");

        Proof memory p = _deserializeProof(proof);

        uint256[] memory publicInputs = new uint256[](3);
        publicInputs[0] = uint256(merkleRoot);
        publicInputs[1] = uint256(groupId);
        publicInputs[2] = uint256(nullifier);

        bool isValid = _verify(p, publicInputs);
        if (isValid) {
            groupNullifiers[groupId][nullifier] = true;
            emit NullifierUsed(groupId, nullifier);
        }

        return isValid;
    }

    /**
     * @notice Core Groth16 verification
     */
    function _verify(
        Proof memory proof,
        uint256[] memory publicInputs
    ) internal view returns (bool) {
        // Compute the linear combination vk_x
        G1Point memory vkX = G1Point(0, 0);

        // vk_x = sum(IC[i] * publicInputs[i])
        for (uint256 i = 0; i < publicInputs.length; i++) {
            require(i < vk.gammaABC.length, "Too many public inputs");
            G1Point memory scaled = _g1MulScalar(vk.gammaABC[i], publicInputs[i]);
            vkX = _g1Add(vkX, scaled);
        }

        // Check pairing: e(A, B) = e(vk_x, gamma) * e(C, delta)
        // In practice: e(A, B) * e(-vk_x, gamma) * e(-C, delta) = 1

        // Negate points for pairing check
        G1Point memory negVkX = _g1Neg(vkX);
        G1Point memory negC = _g1Neg(proof.C);

        // Pairing check using precompile at address 0x8
        // 3 pairings needed
        uint256[24] memory input = [
            // e(A, B)
            proof.A.X, proof.A.Y,
            proof.B.X[1], proof.B.X[0], // B is in Fq2, swapped for precompile
            proof.B.Y[1], proof.B.Y[0],
            // e(-vk_x, gamma)
            negVkX.X, negVkX.Y,
            vk.gamma.X[1], vk.gamma.X[0],
            vk.gamma.Y[1], vk.gamma.Y[0],
            // e(-C, delta)
            negC.X, negC.Y,
            vk.delta.X[1], vk.delta.X[0],
            vk.delta.Y[1], vk.delta.Y[0],
            // 0x8 precompile expects specific format
            0, 0, 0, 0, 0, 0
        ];

        uint256[1] memory out;
        bool success;

        // Call pairing precompile
        assembly {
            success := staticcall(gas(), 0x8, input, 768, out, 32)
        }

        return success && out[0] == 1;
    }

    /**
     * @notice Deserialize proof from bytes
     */
    function _deserializeProof(bytes calldata data) internal pure returns (Proof memory) {
        require(data.length >= 256, "Proof too short");

        uint256[8] memory elements;
        for (uint256 i = 0; i < 8; i++) {
            elements[i] = uint256(bytes32(data[i * 32:(i + 1) * 32]));
        }

        return Proof({
            A: G1Point(elements[0], elements[1]),
            B: G2Point([elements[3], elements[2]], [elements[5], elements[4]]),
            C: G1Point(elements[6], elements[7])
        });
    }

    // G1 arithmetic operations

    function _g1Add(G1Point memory a, G1Point memory b) internal view returns (G1Point memory) {
        uint256[4] memory input = [a.X, a.Y, b.X, b.Y];
        uint256[2] memory output;

        assembly {
            let success := staticcall(gas(), 0x6, input, 128, output, 64)
            if iszero(success) { revert(0, 0) }
        }

        return G1Point(output[0], output[1]);
    }

    function _g1MulScalar(G1Point memory p, uint256 s) internal view returns (G1Point memory) {
        uint256[3] memory input = [p.X, p.Y, s];
        uint256[2] memory output;

        assembly {
            let success := staticcall(gas(), 0x7, input, 96, output, 64)
            if iszero(success) { revert(0, 0) }
        }

        return G1Point(output[0], output[1]);
    }

    function _g1Neg(G1Point memory p) internal pure returns (G1Point memory) {
        return G1Point(p.X, PRIME_Q - (p.Y % PRIME_Q));
    }

    /**
     * @notice Initialize with placeholder VK (should be replaced with real circuit VK)
     */
    function _initializePlaceholderVK() internal {
        // These are placeholder values - must be replaced with actual circuit VK
        vk = VerifyingKey({
            alpha: G1Point(1, 2),
            beta: G2Point([uint256(1), uint256(1)], [uint256(1), uint256(1)]),
            gamma: G2Point([uint256(1), uint256(1)], [uint256(1), uint256(1)]),
            delta: G2Point([uint256(1), uint256(1)], [uint256(1), uint256(1)]),
            gammaABC: new G1Point[](4)
        });

        for (uint256 i = 0; i < 4; i++) {
            vk.gammaABC[i] = G1Point(1, 2);
        }
    }
}

contracts/primitives/Poseidon.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title Poseidon Hash Library
 * @notice Implements Poseidon2 permutation for ZK-friendly hashing
 * @dev Based on the Poseidon permutation with rate=2, capacity=1
 *      Uses 8 full rounds and 57 partial rounds for 256-bit security
 */
library Poseidon {
    // Prime field modulus for BN254 curve
    uint256 constant PRIME = 21888242871839275222246405745257275088548364400416034343698204186575808495617;

    // Round constants for Poseidon2 (simplified - production should use generated constants)
    uint256 constant ROUNDS_F = 8; // Full rounds
    uint256 constant ROUNDS_P = 57; // Partial rounds

    // MDS matrix coefficients (2x2)
    uint256 constant M00 = 5;
    uint256 constant M01 = 7;
    uint256 constant M10 = 1;
    uint256 constant M11 = 2;

    /**
     * @notice Poseidon hash for 2 inputs
     * @param inputs Two field elements to hash
     * @return The Poseidon hash output
     */
    function hash(bytes32[2] memory inputs) internal pure returns (bytes32) {
        uint256[3] memory state;
        state[0] = uint256(inputs[0]) % PRIME;
        state[1] = uint256(inputs[1]) % PRIME;
        state[2] = 0; // Capacity element

        // First full rounds
        for (uint256 i = 0; i < ROUNDS_F / 2; i++) {
            _fullRound(state, i);
        }

        // Partial rounds
        for (uint256 i = 0; i < ROUNDS_P; i++) {
            _partialRound(state, ROUNDS_F / 2 + i);
        }

        // Last full rounds
        for (uint256 i = 0; i < ROUNDS_F / 2; i++) {
            _fullRound(state, ROUNDS_F / 2 + ROUNDS_P + i);
        }

        return bytes32(state[0]);
    }

    /**
     * @notice Poseidon hash for a single input
     */
    function hashSingle(bytes32 input) internal pure returns (bytes32) {
        bytes32[2] memory inputs = [input, bytes32(0)];
        return hash(inputs);
    }

    /**
     * @notice Poseidon hash for 3 inputs
     */
    function hash3(bytes32[3] memory inputs) internal pure returns (bytes32) {
        uint256[4] memory state;
        state[0] = uint256(inputs[0]) % PRIME;
        state[1] = uint256(inputs[1]) % PRIME;
        state[2] = uint256(inputs[2]) % PRIME;
        state[3] = 0;

        // Simplified - use hash twice for now (production needs proper 3-input permutation)
        bytes32 h1 = hash([inputs[0], inputs[1]]);
        return hash([h1, inputs[2]]);
    }

    // Internal round functions
    function _fullRound(uint256[3] memory state, uint256 round) private pure {
        // Apply S-box to all elements
        for (uint256 i = 0; i < 3; i++) {
            state[i] = _sbox(state[i]);
        }
        // Add round constant
        state[0] = addmod(state[0], _roundConstant(round, 0), PRIME);
        state[1] = addmod(state[1], _roundConstant(round, 1), PRIME);
        state[2] = addmod(state[2], _roundConstant(round, 2), PRIME);
        // Apply MDS matrix
        _mds(state);
    }

    function _partialRound(uint256[3] memory state, uint256 round) private pure {
        // Apply S-box only to first element
        state[0] = _sbox(state[0]);
        // Add round constant
        state[0] = addmod(state[0], _roundConstant(round, 0), PRIME);
        // Apply MDS matrix
        _mds(state);
    }

    function _sbox(uint256 x) private pure returns (uint256) {
        // x^5 in the prime field
        uint256 x2 = mulmod(x, x, PRIME);
        uint256 x4 = mulmod(x2, x2, PRIME);
        return mulmod(x4, x, PRIME);
    }

    function _mds(uint256[3] memory state) private pure {
        uint256 s0 = state[0];
        uint256 s1 = state[1];
        uint256 s2 = state[2];

        // 2x2 MDS on first two elements (simplified for rate=2)
        state[0] = addmod(mulmod(M00, s0, PRIME), mulmod(M01, s1, PRIME), PRIME);
        state[1] = addmod(mulmod(M10, s0, PRIME), mulmod(M11, s1, PRIME), PRIME);

        // Mix in capacity element
        state[0] = addmod(state[0], s2, PRIME);
    }

    function _roundConstant(uint256 round, uint256 index) private pure returns (uint256) {
        // Deterministic round constants derived from seed
        // Production: use constants generated by poseidon-paramgen
        return uint256(keccak256(abi.encodePacked("poseidon", round, index))) % PRIME;
    }
}

